package DataTypes;

public class DoubleOperations {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double n1 = 3.14;
		double n2 = 2.72;
		System.out.println("n1=" + (n1) + ", n2=" + (n2));
		System.out.println("n1+n2=" + (n1 + n2));
		System.out.println("n1-n2=" + (n1 - n2));
		System.out.println("n1/n2=" + (n1 / n2));

	}

}
